global.styles:
.cc-window.cc-floating.cc-type-info.cc-theme-classic.cc-bottom.cc-left {
  max-width: calc(100% - 128px);
  padding: 16px 24px;
  left: 64px;
  display: flex;
  flex-direction: row;
  border: 0 !important;
  background: rgba(255, 255, 255, 0.64);
  backdrop-filter: blur(10px);
  gap: 50px;
}

.cc-window.cc-floating.cc-type-info.cc-theme-classic.cc-bottom.cc-left .cc-message {
  margin-bottom: 0;
}

.cc-message .modalCookiesText {
  display: inline;
}

.cc-message .modalCookiesText p {
  color: #000;
  font-family: "Neuro";
  font-size: 10px;
  font-style: normal;
  font-weight: 500;
  line-height: 16px;
  letter-spacing: 2.4px;
  text-transform: uppercase;
  margin-bottom: 8px;
}

.cc-message .modalCookiesText span {
  color: #000;
  font-family: "Neuro";
  font-size: 10px;
  font-style: normal;
  font-weight: 400;
  line-height: 16px;
  letter-spacing: 0.8px;
}

.cc-message a {
  color: #000 !important;
  font-family: "Neuro";
  font-size: 10px;
  font-style: normal;
  font-weight: 400;
  line-height: 16px;
  letter-spacing: 0.8px;
  text-decoration: underline;
}

.cc-window.cc-floating.cc-type-info.cc-theme-classic.cc-bottom.cc-left .cc-compliance {
  padding-top: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.cc-window.cc-floating.cc-type-info.cc-theme-classic.cc-bottom.cc-left .cc-compliance .cc-btn {
  width: 100%;
  border-radius: 2px;
  background: rgba(0, 0, 0, 0.80);
  backdrop-filter: blur(10px);
  color: #FFF;
  text-align: center;
  font-family: "Neuro";
  font-size: 11px;
  font-style: normal;
  font-weight: 500;
  line-height: 24px;
  letter-spacing: 2.64px;
  text-transform: uppercase;
}

@media screen and (max-width: 1024px) {
  .cc-window.cc-floating.cc-type-info.cc-theme-classic.cc-bottom.cc-left {
    flex-direction: column;
    max-width: 100%;
    left: 0;
    padding: 16px !important;
    gap: 0;
  }
}
